mapper.update:
update easybuy_user
set password = #{password}
where loginName = #{loginName}

UPDATE easybuy_user u
SET u.isDel = 1
WHERE u.id = #{id}

update easybuy_user
set loginName = #{loginName},userName=#{userName},password=#{password},roleId = #{roleId},sex = #{sex},email=#{email},mobile= #{mobile}
where id=#{id}
